  String? title;
  String? define;
  String? treatpt;

  PrimaryTreatmentData({
    required this.title,
    required this.define,
    required this.treatpt,
  });
}

List<PrimaryTreatmentData> primaryTreatmentDataList = [
  PrimaryTreatmentData(
      title: "মাথাব্যাথা",
      define:
          "মাথাব্যথা (ইংরেজি: Headache) খুবই সাধারণত একটি সমস্যা।প্রত্যেক মানুষই জীবনের কোনো না কোনো সময় মাথাব্যথায় আক্রান্ত হয়ে থাকেন। মাথাব্যথা আসলে কোনো রোগ নয় বরং একটি উপসর্গ মাত্র। মাথাব্যথার অনেক রকমভেদ আছে যেমন মাইগ্রেন, টেনশন-টাইপ মাথাব্যথা, ক্লাস্টার মাথাব্যথা ইত্যাদি।[১] ঘনঘন মাথাব্যথা প্রাত্যহিক পারিবারিক ও কর্মজীবনকে বিষাদময় করে তুলতে পারে।[১] এছাড়া তীব্র মাথাব্যথা ডিপ্রেশনের ঝুঁকি বাড়ায়।[১]\nমাথাব্যথার অনেক কারণ থাকতে পারে। মাথাব্যথার খুব সাধারণ কারণগুলো হলো ক্লান্তি, পর্যাপ্ত ঘুমের অভাব, মানসিক চাপ, ঔষধের প্রতিক্রিয়া, সর্দি, সাইনুসাইটিস, মাথায় আঘাত, দাঁতের রোগ, খুবই ঠাণ্ডা পানীয় বা খাবার খুব দ্রুত খেয়ে ফেলা ইত্যাদি। রোগ নির্ণয়ের সুবিধার্থে মাথাব্যথাকে প্রাইমারি ও সেকেন্ডারি এই ক্যাটাগরিতে ভাগ করা হয়। মাইগ্রেন, টেনশন মাথাব্যথা ইত্যাদি প্রাইমারি ক্যাটাগরিতে পড়ে অপরদিকে মাথায় আঘাত বা টিউমার, ইনফেকশন ইত্যাদি সেকেন্ডারি ক্যাটাগরিতে পড়ে।\nসূর্যের আলোয় বেরলে মাইগ্রেন হয়।\nনির্দিষ্ট কোনও গন্ধ পেলে মাথাব্যথা হয়।\nএকটি নির্দিষ্ট সময়েই মাথাব্যথা করে।",
      treatpt:
          "\n\nকোন কোন পানীয়?\nকফি: মাথাব্যথা ও মাইগ্রেন কমাতে চাইলে পান করতে পারেন কফি। কফির মধ্যে উপস্থিত ক্যাফিন এক্ষেত্রে দারুণ কাজ করে। এছাড়া চাইলে ব্ল্যাক টি-ও পান করা যেতে পারে।\nলেবুর রসের সঙ্গে বিট নুন: মাথাব্যথা থেকে হাতেনাতে মুক্তি চাইলে লেবুর রস জলে মিশিয়ে নিন। তারপর মেশান সামান্য বিট নুন। দেখবেন ভালো রয়েছেন। তেব প্রেশার থাকলে নুন মেশাবেন না।\nতুলসীপাতা: তুলসীর গুণগুণের শেষ নেই। তবে আপনি চাইল তুলসীপাতা বেটে নিয়ে সেই রস পান করতে পারেন। এছাড়া তুলসীপাতার রসের জ্যুসও বানানো যায়। এই রসে থাকা বিভিন্ন উপকারী উপাদান আপনার সমস্যা কমাতে পারে।\nগুড়ের সঙ্গে জিরে: গুড়ের সঙ্গে মিশিয়ে নিন জিরে। তারপর সেই মিশ্রণ মিশিয়ে দিন জলে। এই পানীয় খেয়ে দেখুন, সমস্যা দূর হবেই হবে। তবে ডায়াবিটিস থাকলে এই পানীয় থেকে দূরে থাকুন।\nআদার চা: যে কোনও ধরনের মাথাব্যথা কমাতে চাইলে আদার চা হতে পারে আপনার অন্যতম হাতিয়ার। সেক্ষেত্রে লিকার চায়ে মেশাতে হয় কুচো আদা।\nদুধ: মাইগ্রেনের সমস্যায় দিনে দুধ একগ্লাস করে পান করলে মিলতে পারে সমস্যা থেকে মুক্তি। দুধের মধ্যে থাকা বিশেষকিছু উপাদান আপনার মাথাব্যথা কমাতে সাহায্য করে।"),
  PrimaryTreatmentData(
      title: "সাপে কাটা",
      define:
          "সাপে কাটার সঙ্গে ‘আতঙ্ক’ শব্দটি জড়িয়ে আছে। গ্রামীণ জীবনে সাপে কাটার বিষয়টি নিত্যনৈমিত্তিক ঘটনা হলেও মে থেকে অক্টোবর মাসে তা বেড়ে যায়। তবে সাপে কাটলেই যে বিষক্রিয়া হবে, বিষয়টা কিন্তু এমন নয়। অনেকের জানা, দেশে বিষধর সাপের চেয়ে নির্বিষ সাপের সংখ্যাই বেশি। কিন্তু অপচিকিৎসা ও অজ্ঞতার কারণে আক্রান্ত মানুষের জীবন বাঁচানো সম্ভব হয় না।\nবিশ্ব স্বাস্থ্য সংস্থার এক প্রতিবেদনে বলা হয়েছে, বাংলাদেশে প্রতি বছর ৫ লাখ ৮৯ হাজার ৯১৯ জন সাপের কামড়ের শিকার হয়। এদের মধ্যে বছরে মারা যায় ৬ হাজার ৪১ জন। আশঙ্কার বিষয় হলো, এর মধ্যে চিকিৎসকের শরণাপন্ন হয় মাত্র ৩ শতাংশ মানুষ।\nবিষধর সাপে কাটলে শরীরে বিষক্রিয়ার কিছু লক্ষণ দেখা যায়: ক্ষতস্থানে বিষদাঁতের দুটি দংশনের চিহ্নের উপস্থিতি, ক্ষতস্থান থেকে অনবরত রক্তপাত ও ক্ষতস্থান অস্বাভাবিকভাবে ফুলে ওঠা এবং প্রচণ্ড ব্যথা অনুভব করা, কখনো কখনো সারা শরীর ফুলে যাওয়া, খাবার ও ঢোক গিলতে অসুবিধা, শ্বাসকষ্ট, চোখে ঝাপসা দেখা ও চোখের পাতা বন্ধ হয়ে আসা, ঘুম ঘুম ভাব আসা, হাত-পা অবশ হয়ে আসা ও অচেতন হয়ে পড়া, ঘাড় সোজা রাখতে না পারা, প্রস্রাব বন্ধ হয়ে যাওয়া প্রভৃতি।",
      treatpt:
          "\n\nএ ধরনের কোনো লক্ষণ দেখা দিলে নিকটস্থ হাসপাতালে যথাযথ চিকিৎসা গ্রহণ করা উচিত।\nসাপে কাটা ব্যক্তিকে প্রথমে আশ্বস্ত করতে হবে যে তার ভয়ের কোনো কারণ নেই। এর বৈজ্ঞানিক চিকিৎসা আছে। বেশির ভাগ সাপই নির্বিষ, এমনকি বিষধর সাপের পক্ষেও দংশনের সময় সব সময় প্রচুর পরিমাণ বিষ ঢেলে দেওয়া সম্ভব হয় না। এ জন্য আক্রান্ত ব্যক্তিকে সাহস দেওয়া প্রয়োজন। প্রাথমিক চিকিৎসার পর হাসপাতালে দ্রুত নিতে হবে। হাসপাতালে স্থানান্তরের সময় আক্রান্ত ব্যক্তিকে হাঁটতে না দিয়ে কাঁধে, খাটিয়ায় বা কোনো যানবাহনের সাহায্যে হাসপাতালে নিয়ে যেতে হবে। সম্ভব হলে সাপটি দেখতে কেমন, তা লক্ষ করা। সাপের বর্ণনা চিকিৎসককে চিকিৎসা পরিকল্পনায় সাহায্য করতে পারে।\nদংশিত স্থান কিছুতেই কাটাছেঁড়া করা উচিত নয়। কেবল ভেজা কাপড় দিয়ে কিংবা জীবাণুনাশক মলম দিয়ে ক্ষতস্থান মুছে দিতে হবে। আক্রান্ত স্থান থেকে মুখের সাহায্যে রক্ত বা বিষ টেনে বের করার চেষ্টা করা বা ক্ষতস্থানে গোবর, শিমের বিচি, আলকাতরা, লালা, ভেষজ ওষুধ বা কোনো প্রকার রাসায়নিক লাগানো উচিত নয়। দংশন করা স্থান থেকে ওপরের দিকে একটি লম্বা কাঠ এবং গামছা বা কাপড় দিয়ে কেবল একটি বাঁধন এমনভাবে দিতে হবে, যেন তা খুব আঁটসাঁট বা ঢিলে কোনোটাই না হয় এবং একটি আঙুল একটু চেষ্টায় বাঁধনের নিচ দিয়ে যেতে পারে। কারণ, খুব বেশি শক্ত করে বাঁধলে আক্রান্ত অঙ্গে রক্ত চলাচল সম্পূর্ণ বন্ধ হয়ে যেতে পারে। যেহেতু মাংসপেশির সংকোচনে বিষ দ্রুত শরীরে ছড়িয়ে পড়ে, তাই সাপে কাটার স্থান বেশি নড়াচড়া করা উচিত নয়। আক্রান্ত ব্যক্তিকে বমি করানোর জন্য বিভিন্ন পদ্ধতি প্রয়োগ কিংবা কানের ভেতর বা চোখের ভেতর কিছু ঢেলে দেওয়া থেকেও বিরত থাকতে হবে। আশপাশের মানুষের একটু সচেতনতা ও বিজ্ঞানমনস্কতাই সাপে কাটা রোগীর প্রাণ ফিরিয়ে দিতে অনেকাংশে সাহায্য করবে।"),
  PrimaryTreatmentData(
      title: "হাড় ভাঙ্গা",
      define:
          "হাড় গঠনের জন্য প্রয়োজনীয় খনিজ হলো প্রধানত ক্যালসিয়াম ও ফসফেট। হাড়, অস্থিসন্ধি ও মাংসপেশী ইত্যাদি মিলে পূর্ণাঙ্গ মানবদেহের কাঠামো গঠিত হয়। হাড় ও অস্থিসন্ধিসমূহ একটি নির্দিষ্ট দিকে ও নির্দিষ্ট নিয়মে নড়াচড়া করতে পারে। এ নির্দিষ্ট নিয়মের ব্যতিক্রম ঘটলেই হাড় ভেঙে যায়। হাড়ের সবচেয়ে বাইরের আবরণটির নাম পেরিঅস্টিয়াম। এই পেরিওস্টিয়ামের আংশিক বা সম্পূর্ণ ভেঙে যাওয়াকে ফ্র্যাকচার বলে। খুব সাধারণ ভাষায় হাড় ফেটে যাওয়া বা ভেঙে যাওয়াই ফ্র্যাকচার হিসেবে পরিচিত।",
      treatpt:
          "\n\nহাড় ভাঙার ক্ষেত্রে কিছু সাবধানতা–\n১. যেকোনো প্রকার নড়াচাড়ার আগে রোগী ভাঙা জায়গা যেন না নাড়াতে পারে, সে ব্যবস্থা করতে হবে। সম্ভব হলে লাঠি বা শক্ত কিছু দিয়ে বাঁধার ব্যবস্থা করতে হবে, যেন ভাঙা অংশে কোনো নড়াচাড়া না হয়। এতে স্থানটি আবার আঘাত পাওয়া থেকে রক্ষা পাবে। শুধু তা-ই নয়, এতে হাড়ের ভাঙাপ্রান্ত, রক্তনালী, স্নায়ু বা মাংসপেশীকেও ক্ষতিগ্রস্ত করতে পারবে না। তবে বাঁধন দেওয়ার সময় অবশ্যই খেয়াল রাখতে হবে যেন রক্ত সঞ্চালন বন্ধ না হয়ে যায়। ফ্র্যাকচার স্থির করা যায় ব্যান্ডেজ কিংবা স্প্লিন্টের মাধ্যমে।স্বাভাবিক ক্ষেত্রে সাধারণ ব্যান্ডেজই যথেষ্ট। কিন্তু অঙ্গবিকৃতি ঘটলে বা দুর্ঘটনা ঘটলে রোগীকে হাসপাতালে পাঠানোর জন্য, বিশেষ করে রোগীর হাতে ও পায়ে স্প্লিন্ট বেঁধে দিতে হবে, যাতে স্থানটি নড়াচড়া থেকে বিরত থাকে। সতর্ক থাকতে হবে যেন কখনোই ফ্র্যাকচারের ঠিক ওপরের স্থানে ব্যান্ডেজ না বাঁধা হয়। আবার স্প্লিন্ট এমন লম্বা হতে হবে, যাতে অস্থিসন্ধির ওপরের ও ফ্র্যাকচারের নিচের অংশকে নিশ্চল রাখতে পারে। স্প্লিন্ট ভালো প্যাডযুক্ত হতে হবে, যাতে হাত বা পায়ের সাথে ঠিকমতো খাপ খায়। এটি পর্যাপ্ত চওড়া হবে। জরুরি ক্ষেত্রে স্প্লিন্ট হিসেবে হাঁটার জন্য ব্যবহৃত লাঠি, কাঠের টুকরো কিংবা হার্ডবোর্ডের টুকরোও ব্যবহার করা যেতে পারে।"
          "\n২. বাহ্যিক ক্ষত থেকে রক্তক্ষরণ হলে তা বন্ধের ব্যবস্থা করতে হবে।\n৩. খেয়াল রাখতে হবে যেন রোগী আরামদায়ক অবস্থায় থাকতে পারেন। ব্যথার মধ্যে রোগীকে যতটা সম্ভব স্বস্তি দেওয়া যায়, সে ব্যবস্থা করতে হবে। সম্ভব হলে ভাঙা জায়গায় বরফ দেওয়া যেতে পারে, তাতে ক্ষতস্থান অবশ হয়ে ব্যথা কিছুটা কমতে পারে।\n৪. আঘাতজনিত কারণে রোগী বমি করে ফেললে, যতটা সম্ভব কম নড়াচাড়া করে রোগীকে বাম কাত করে বমি করানোর ব্যবস্থা করুন। অন্যথায় পাকস্থলি থেকে উঠে আসা এসব অর্ধপাচ্য খাবার ফুসফুসে চলে গেলে অবস্থা আরও জটিল হয়ে যেতে পারে।\n৫. রোগী অচেতন থাকলে শ্বাস-প্রশ্বাস ও হৃদস্পন্দন পরীক্ষা করে দেখে রোগীকে প্রয়োজনে কৃত্রিম শ্বাস-প্রশ্বাস ও বুকে চাপ দিয়ে সিপিআর দেওয়ার ব্যবস্থা করুন।\n৬. রোগী সচেতন থাকলে তিনি প্রচণ্ড ব্যথার মধ্যে থাকবেন। তাই রোগীকে মানসিকভাবে শক্ত রাখার ব্যবস্থা নিতে হবে। রোগীকে আশ্বস্ত করুন ও ধৈর্য ধারনের মাধ্যমে অহেতুক তীব্র দুশ্চিন্তা থেকে রোগীকে বিরত রাখুন।"
          "\nমাথার খুলির হাড় ভেঙে গেলে: রোগীর মাথা ও কাঁধ সামান্য উঁচু করে রোগীকে শুইয়ে রাখবেন। রোগীকে কোনো ধরনের নড়াচাড়া করা থেকে বিরত থাকুন। যদি স্থানান্তর করতে হয়, তবে অনেক সাবধানে দুই হাত দিয়ে রোগীর মাথা স্থির রেখে অবলম্বন ব্যবহার করে স্থানান্তর করুন। শরীরের কাপড় আলগা করে দিন, এতে ব্যর্থ হলে প্রয়োজনে কিছু দিয়ে কাপড় কেটে ফেলুন। রোগীর তাপমাত্রা ঠিক রাখতে চেষ্টা করুন। দরকার হলে হাতের তালুতে ও পায়ের তলায় গরম সেঁক দিতে পারেন। মাথায় কিছু প্রবেশ করে মাথার ভেতরেই থেকে গেলে কোনক্রমেই তা বের করার চেষ্টা করবেন না। সামান্য এদিক সেদিক হলে রোগীর অপূরণীয় ক্ষতি হয়ে যেতে পারে। কোনো অবস্থায়ই রোগীকে পানি পান করতে বা কোনো কিছু খেতে দিবেন না। তাঁকে দ্রুত প্রয়োজনীয় সুযোগ-সুবিধা সম্বলিত সদর হাসপাতাল বা মেডিকেল কলেজ হাসপাতালে নিয়ে যাওয়ার ব্যবস্থা করুন।\nমেরুদণ্ডের হাড় ভেঙে গেলে: মেরুদণ্ডের ঘাড়ের দিকের অংশ ভেঙে গেলে রোগী জ্ঞান হারাতে পারেন। এ অবস্থায় রোগী যেন শ্বাস-প্রশ্বাস চালিয়ে যেতে পারেন সেদিকে খেয়াল রাখতে হবে। প্রয়োজনে কৃত্রিম শ্বাস-প্রশ্বাসের ব্যবস্থা করতে হবে। কৃত্রিম শ্বাস-প্রশ্বাস দিতে হলে কোনক্রমেই রোগীর মাথা ও ঘাড় নাড়াচাড়া করা যাবে না। আঙুল দিয়ে আলতো করে নিচের চোয়াল আলগা করে কৃত্রিম শ্বাসের ব্যবস্থা করতে হবে। বমির কারণে রোগীকে বামপাশে কাত করতে হলে একাধিক জন মিলে একসাথে রোগীকে সাবধানে সামান্য কাত করতে পারেন। কিন্তু খেয়াল রাখতে হবে যেন মাথা, ঘাড় আর মেরুদণ্ড একই কলামে থাকে। রোগীকে স্থানান্তর করতে হলে অবশ্যই চিৎ করে শোয়াতে হবে। এজন্য স্ট্রেচার আবশ্যক। স্ট্রেচার না পাওয়া গেলে শক্ত বড় কাঠের তক্তা বা এই জাতীয় জিনিস ব্যবহার করা যেতে পারে। তবে কোনো অবস্থায়ই বেশি নাড়াচাড়া করা যাবে না। মেরুদণ্ড স্বাভাবিক অবস্থায় রাখতে পিঠের নিচে কিছু অংশে বালিশ জাতীয় নরম কিছু দিয়ে ঠেস দিয়ে রাখা যেতে পারে। পাশাপাশি কোনো অবস্থায়ই মেরুদণ্ডে যেন টান না লাগে, সে দিকে লক্ষ্য রাখতে হবে। মাথা ও ঘাড় যেন কোনোভাবে না নড়ে যায়, সেজন্য সাবধানতা অবলম্বন করতে হবে।"
          "\nকলার বোন ভেঙে গেলে: আমাদের কাঁধের কাছে এ অস্থির অবস্থান। যা হাতের ওজনকে কেন্দ্রীয় কঙ্কালে সঞ্চালন করতে সাহায্য করে। হাড়টি কোনো কারণে ভেঙে গেলে ভাঙা হাড়ের দিকের বগলে নরম কাপড় বা প্যাড দিয়ে ঊর্ধ্ববাহুকে স্বাভাবিক অবস্থায় এনে মোটা ব্যান্ডেজের সাহায্যে বেঁধে দিতে হবে। তারপর নিম্নবাহু বুকের উপর রেখে আরেকটি কাপড়ের সাহায্যে সুস্থ হাতের দিকে কাঁধের উপর বেঁধে দিতে হবে, যেন তা নাড়াচাড়া না করতে পারে। এছাড়া ‘কলার অ্যান্ড কাফ স্লিং’ও ব্যবহার করতে পারেন। এতে ভাঙা স্থানও স্বাভাবিক অবস্থানে থাকবে।\nঊর্ধ্ববাহু ভেঙে গেলে: আহত হাতের নিম্নবাহু ভাঁজ করে এমনভাবে রাখতে হবে, যেন আঙুল কাঁধ স্পর্শ করে। সম্ভব হলে হাত ও বুকের মাঝখানে একটি নরম কাপড় বা প্যাড দেওয়া যেতে পারে। এবার হাতটি গলার সাথে মোটা ব্যান্ডেজের সাহায্যে ঝুলিয়ে দিতে হবে। একটি ঊর্ধ্ববাহুর দৈর্ঘের সমান সোজা লাঠির সাথে ভাঙা হাত ও শরীর ব্যান্ডেজের সাহায্যে পেঁচিয়ে দিন, একটি কাঁধের নিচ দিয়ে, আরেকটি কনুইয়ের উপর দিয়ে। যদি একাধিক স্থানে হাড় ভাঙা থাকে এবং হাত ভাঁজ না করা যায়, তবে হাত সোজা করে শরীরের পাশে নিয়ে ব্যান্ডেজ দিয়ে শরীর ও বাহু জড়িয়ে, শরীর ও কনুই জড়িয়ে এবং কব্জি ও উরু জড়িয়ে রাখতে হবে।\nনিম্নবাহু ভেঙে গেলে: কনুই ভাঁজ করার মাধ্যমে ঊর্ধ্ববাহুর সাথে সমকোণ তৈরি করে রোগীর নিম্নবাহু এমনভাবে রাখুন যেন হাতের তালু শরীরের দিকে আর বুড়া আঙুল উপরের দিকে থাকে। এরপর দুটি শক্ত কাঠি ব্যবহার করে ব্যান্ডেজের সাহায্যে রোগীর নিম্নবাহু পেঁচিয়ে ফেলুন, যেন হাড় ঠিক স্থানে সোজা হয়ে থাকে। শেষে রোগীর হাত একটি কাপড়ের সাহায্যে গলার সাথে ঝুলিয়ে দিন।\nহাঁটু বা পায়ের অন্য কোথাও ভেঙে গেলে: প্রথমে রোগীকে চিৎ করে শুইয়ে দিন। ভাঙা পায়ের নিচে বা দুই পাশে, পায়ের উপর থেকে নিচ পর্যন্ত একটি লম্বা কাঠের তক্তা বা সোজা লাঠি রেখে ব্যান্ডেজের সাহায্যে উরুতে একটি বাঁধন এবং গোড়ালি ও পায়ের পাতাজুড়ে একটি বাঁধন দিন। এরপর পা স্থির রাখার জন্য যতটা প্রয়োজন, পায়ের বাকি অংশে ততটা বাঁধ দিন। ভাঙা স্থানের কাছে বাঁধার সময় সাবধানতা অবলম্বন করুন। হাঁটু ভেঙে গেলে হাঁটুর ঠিক উপরে একটি ব্যান্ডেজের মধ্যভাগ রেখে তা পেঁচিয়ে হাঁটুর ঠিক নিচে এসে গিঁট দিন। তক্তা ও পায়ের মাঝে প্রয়োজনমত প্যাড বা নরম কাপড় ব্যবহার করুন। সব অবস্থায়ই যেন রোগীর ভাঙা পা একটু উঁচু করে রাখা যায়, সেই ব্যবস্থা করতে হবে।"
          "\nপায়ের পাতা ভেঙে গেলে: প্রথমে গোড়ালি থেকে পায়ের আঙুল পর্যন্ত দীর্ঘ একটি কাঠের তক্তা নিন। এরপর ব্যান্ডেজের সাহায্যে তক্তাটিকে পায়ের সাথে ভালোভাবে বাঁধুন। এবার আরেকটি ব্যান্ডেজের সাহায্যে পায়ের গোড়ালিসহ পায়ের পাতা পেঁচিয়ে ফেলুন। খেয়াল রাখতে হবে, পা সব সময় যেন একটু উঁচুতে থাকে।\nহাড় ভাঙা ছাড়াও অস্থিসন্ধির স্থানচ্যুতি এবং হাত-পা মচকে যাওয়া প্রাত্যহিক জীবনে সাধারণ ঘটনা। একটি হাড় আরেকটি হাড়ের সাথে যেখানে সংযুক্ত হয়েছে তাকে অস্থিসন্ধি বলে। অনেক সময় আঘাত পেলে বা হঠাৎ টান পড়লে হাড় তার অস্থিসন্ধি থেকে ছুটে যেতে পারে। এ অবস্থায় যথেচ্ছ টানাটানি করে হাড়কে পূর্বাবস্থানে আনার চেষ্টা না করে যেভাবে রোগী আরাম পায় সেভাবে তাকে রাখার ব্যবস্থা করতে হবে এবং যত দ্রুত সম্ভব তাকে নিকটস্থ হাসপাতালে নিয়ে যেতে হবে। আর কোথাও মচকে গেলে ওই মচকানো জায়গা নাড়াচাড়া না করে যতটা সম্ভব আরামদায়ক অবস্থায় রাখা যায়, তা নিশ্চিত করতে হবে। মচকানো জায়গায় বরফ দেওয়া যেতে পারে। সম্ভব হলে ক্রেপ ব্যান্ডেজ দিয়ে বেঁধে দেওয়া যায়। এতে মচকে যাওয়া স্থানে নাড়াচাড়া কম হবে এবং ওই স্থানে পরবর্তী কোনো আঘাত পাওয়ার সম্ভাবনাও কমে যাবে।"),
  PrimaryTreatmentData(
      title: "ক্ষত",
      define:
          "থমিক চিকিৎসা বাক্স (ইংরেজিতে ফার্স্ট এইড বক্স নামে পরিচিত) একটি ছোট বাক্স বা থলি যাতে প্রাথমিক চিকিৎসার জন্য সবরকম জরুরী উপকরণ মজুদ থাকে। প্রয়োজনের সময় প্রাথমিক চিকিৎসা বাক্স সাথে থাকলে খুব দ্রুত ও সহজেই যেকোন দূর্ঘটনার মোকাবেলা করা যায়। বাড়িতে, গাড়িতে এবং কর্মস্থলে প্রাথমিক চিকিৎসা বাক্স থাকা জরুরি। হয়তো কোন এক সময় এই বাক্সটি হতে পারে অসময়ের ভাল বন্ধু।",
      treatpt:
          "\n\nপ্রয়োজনীয় উপকরণ\nজীবাণুমুক্ত গজ কাপড়\nক্ষত হতে রক্ত পড়া বন্ধ করে এবং বাহ্যিক জীবাণুর সংক্রমণ থেকে রক্ষা করে। এটা ক্ষত হতে তরল পদার্থ শুষে নেয় ও ক্ষত স্থান নিরাপদ রাখে।এটি সম্পর্কে আমাদের জ্ঞান রাখা প্রয়োজন।\nরোলার ব্যান্ডেজ\nসাধারণত ড্রেসিংকে জায়গা মতো আটকে রাখতে ও ব্যান্ডেজে চাপ দিয়ে রক্তপাত বন্ধ করতে হলে রোলার ব্যান্ডেজ ব্যবহার করা হয়।\nকাঁচি ও চিমটা\nব্যান্ডেজ কাটতে এবং ক্ষতস্থানে চুল বা লোম কাটতে কাঁচির প্রয়োজন হয়। ক্ষতে কাঁচের টুকরা, ময়লা কিমবা কাঁটা উঠাতে চিমটা ব্যবহার হয়।\nহাতমোজা\nহাত জীবাণুমুক্ত রাখতে এবং ক্ষতস্থানে হাতের প্রয়োগ করতে হাতমোজার প্রয়োজন হয়।"
          "\nলিউকোপ্লাস্ট\nব্যান্ডেজকে ক্ষতের উপর আটকাতে লিউকোপ্লাস্টের দরকার হয়।\nপচনরোধক মলম\nক্ষতস্থান পরিষ্কার ও জীবাণুমুক্ত করতে জীবাণুনাশক ও পচনরোধক মলম (অ্যান্টিসেপটিক ক্রিম) ব্যবহার করা হয়। যেমন- স্যাভলন, ডেটল, হাইড্রোজেন-পার-অক্সাইড, পভিসেভ ইত্যাদি।\nক্রেপ পট্টি\nহাড় ভেঙে গেলে বা মচকে গেলে ক্রেপ পট্টি (এক ধরনের ভাঁজযুক্ত পট্টি) ব্যবহার করা হয়। এতে উক্ত স্থান নাড়াচাড়া কম হয় যাতে হাড় সরে যাওয়া ও ব্যথা পাওয়ার সম্ভাবনা কম থাকে।\nদগ্ধ মলম\nপোড়া জায়গার ব্যথা কমাতে এবং ঘা শুকাতে দগ্ধ মলম (বার্ন ক্রিম) ব্যবহার করা হয়। যথা- সিলভারজিন ক্রিম। অ্যালোভেরা জেলও পোড়া, চুলকানি ও চামড়ায় র‌্যাশ হলে ব্যবহার করা হয়। ক্যালেন্ডুলা ও আরটিকা ইউরেন্স বার্ন ক্রিম দ্রুত ব্যথা কমায়।"
          "\nব্যথার ওষুধ\nপ্রাথমিক চিকিৎসা বাক্সে ব্যথানাশক ওষুধ থাকা খুব প্রয়োজন। যেমন- প্যারাসিটামল, আইবুপ্রফেন, ডাইক্লোফেনাক ইত্যাদি। ব্যথার ওষুধের সাথে কিছু গ্যাস্ট্রিক (পাকস্থলীর) ওষুধ রাখা ভাল।\nঅ্যান্টিহিস্টামিন\nহিস্টাসিন, ফেক্সােফেনাডিন ইত্যাদি ওষুধ প্রাথমিক চিকিৎসা বাক্সে থাকা জরুরি। অ্যালার্জিজনিত প্রতিক্রিয়া, সর্দি, হাঁচি, কাশি, চুলকানি ও পোকামাকড়ের কামড়ের চিকিৎসায় কাজে লাগে।\nনিরাপদ পিন\nব্যান্ডেজ আটকাতে এবং ক্ষতের কাঁটা বা ময়লা উঠাতে প্রয়োজন হয়।"
          "\nতাপমান যন্ত্র বা থার্মোমিটার\nরোগীর দেহের তাপমাত্রা মাপতে ও জ্বরের পরিমাপ করতে তাপমান যন্ত্র বা থার্মোমিটার ব্যবহার হয়।\nরক্তচাপ পরীক্ষণ যন্ত্র\nরোগীর রক্তচাপ মাপতে ব্যবহার করা হয়। সম্ভব হলে রক্তচাপ পরিমাণ যন্ত্র সর্বদা প্রাথমিক চিকিৎসা বাক্সে রাখা উচিত।\nলেখার খাতা\nজরুরি ফোন নম্বর ও মন্তব্য লেখার জন্য। বিশেষ করে ডাক্তর ও অ্যাম্বুলেন্স নম্বর রাখার জন্য।"),
  PrimaryTreatmentData(
      title: "বিষ পান",
      define:
          "কেউ বিষপান করলে কিংবা বিষক্রিয়া ঘটলে প্রথমেই উচিত তাকে ধারের কাছে হাসপাতালে পাঠানো। তবে তার আগে জানতে হবে, রোগী বিষ বলে যা খেয়েছে তা আসলে কী? সে বমি করেছে কি না? তার বমির সঙ্গে পাতলা পায়খানা হয়েছে কি না?\nমাথাব্যথা, মাথা ঝিমঝিম কিংবা তন্দ্রাভাব রয়েছে কি না? শ্বাসকষ্ট বা খিঁচুনির লক্ষণ রয়েছে কি না? রোগীর শ্বাস-প্রশ্বাস কিংবা বমিতে কোনো রাসায়নিক পদার্থের গন্ধ রয়েছে কি না? তার জ্ঞান আছ রয়েছে কি না?",
      treatpt:
          "\n\nএসব দ্রুত পরীক্ষা করার পর যা করণীয়, সচেতন রোগীর ক্ষেত্রে :\nরোগীর যদি জ্ঞান থাকে এবং খিঁচুনি না থাকে, তাহলে তার বিষকে পাতলা করার জন্য তাকে এক গ্লাস পানি বা দুধ পান করাতে হবে। যদি এতে তার বমি বমি ভাব আসে, তাহলে পান করানো বন্ধ করে দিতে হবে।\nবিষ বলে সে যা খেয়েছে বা পান করেছে, তার মোড়ক বা পাত্রটি শনাক্তকরণের জন্য সংগ্রহ করতে হবে। যদি রোগী বমি করে থাকে, তাহলে তা পরীক্ষা করার জন্য সংগ্রহ করতে হবে।\nএরই মধ্যে কাছের হাসপাতালের জরুরি বিভাগে ফোন করে জানতে হবে যে আপনি সেখানে একজন বিষাক্রান্ত রোগী নিয়ে যাচ্ছেন।\nরোগী যদি এরই মধ্যে অজ্ঞান হয়ে পড়ে, তাহলে তার শ্বাসনালি খোলা রাখতে হবে। প্রয়োজনে কৃত্রিম শ্বাস-প্রশ্বাসের ব্যবস্থা করতে হবে। যত দ্রুত সম্ভব হাসপাতালে ফোন করুন অ্যাম্বুলেন্স পাঠানোর জন্য কিংবা রোগীকেই যত দ্রুত সম্ভব হাসপাতালে পাঠান।\nবি. দ্র.—অনেকে বিষ খাওয়া রোগীকে বমি করানোর চেষ্টা করেন। কিন্তু রোগী যদি পেট্রল, কেরোসিন, এসিড বা ক্ষারজাতীয় কিছু খেয়ে থাকে, তাহলে রোগীকে বমি করানোর চেষ্টা করা উচিত নয়। ঘুমের বড়ি, এসপিরিন, কুইনাইন প্রভৃতি ওষুধ অতিরিক্ত খেলে তাকে গলায় আঙুল বা চামচের ভোঁতা প্রান্ত দিয়ে বমি করানো যেতে পারে। তবে বমি করানোর চেষ্টা রোগীর জন্য অনেক সময় বিপদ ডেকে আনে। এতে নিউমোনিয়া হওয়ার আশঙ্কা থাকে। বমি করানোর ভালো পন্থা হলো এক গ্লাস পানি বা মিষ্টি তরলপদার্থ এক চা চামচ এপসম লবণ মিশিয়ে পান করানো। তবে রোগী অচেতন থাকলে কিছুতেই বমি করানো যাবে না। রোগীকে ভিনেগার কিংবা লেবুর রস পান করানো যাবে না।"
          "\n\nঅচেতন রোগীর ক্ষেত্রে :\nরোগীর শ্বাসনালি উন্মুক্ত রাখতে হবে।\nযত দ্রুত সম্ভব হাসপাতালে ফোন করে ইমার্জেন্সি স্কোয়াডে পাঠাতে বলতে হবে।\nপ্রয়োজনে রোগীকে কৃত্রিম শ্বাস-প্রশ্বাসের ব্যবস্থা করতে হবে।\nসন্দেহজনক বিষ হলে পাত্রটি সংগৃহীত করতে হবে।\nরোগী বমি করলে সেই বমি নমুনা সংগ্রহ করে রাখতে হবে।\nরোগীকে পানি কিংবা তরল কিছুই খেতে দেওয়া যাবে না।\nরোগীকে বমি করানোর চেষ্টা করা যাবে না। যদি রোগী বমি করতে থাকে, তাহলে তার মাথাটা এক পাশে কাত করে দিতে হবে, যাতে বমির পদার্থগুলো মুখ দিয়ে গড়িয়ে পড়তে পারে।\nইমার্জেন্সি স্কোয়াডের ব্যবস্থা না থাকলে আপনি নিজেই রোগীকে দ্রুত হাসপাতালে নিয়ে যান।"),
  PrimaryTreatmentData(
      title: "পানিতে ডোবা",
      define:
          "কেউ পানিতে ডুবে গেলে প্রথমেই যা করবেন\nকারণ: পানিতে ডুবে মৃত্যুর প্রধান কারণ হলো শ্বাসক্রিয়া বন্ধ হয়ে যাওয়া। পানি শ্বাসনালী ও ফুসফুসে ঢুকে গেলে মূলত এমনটি হয়। এ ছাড়া ল্যারিংক্স ও শ্বাসনালীর অনৈচ্ছিক পেশীর তীব্র সংকোচনেও শ্বাসনালীর মুখ বন্ধ হয়ে যেতে পারে। ২ থেকে ৩ মিনিট শ্বাস বন্ধ থাকলে মস্তিষ্কের অপূরণীয় ক্ষতি হয়। ৪ থেকে ৬ মিনিট শ্বাসক্রিয়া বন্ধ থাকলে মৃত্যু পর্যন্ত হয়। শ্বাসক্রিয়া বন্ধ হওয়া ছাড়াও প্রচুর পানি পান করার কারণে রোগীর পাকস্থলী ফুলে যায়।",
      treatpt:
          "\n\nকরণীয়: পানিতে ডুবে মৃত্যু প্রতিরোধযোগ্য। এজন্য এ ধরনের মৃত্যু ঠেকাতে কিছু বিষয় অনুসরণ করতে হবে, যেমন-\n১. কেউ পানিতে ডুবে গেলে আতঙ্কিত না হয়ে প্রথমে তাকে পানি থেকে তুলে আনুন। এক্ষেত্রে প্রতিটি সেকেন্ড মূল্যবান। পানি থেকে তুলে আনার ক্ষেত্রে ভালোভাবে খেয়াল রাখতে হবে, অনেক সময় উদ্ধারকারীও বিপদে পড়তে পারেন। ডুবন্ত ব্যক্তি অবলম্বন পেলেই আঁকড়ে ধরে এবং উপরে ওঠার চেষ্টা করে। ফলে ডুবন্ত ব্যক্তি উদ্ধারকারীকে সজোরে জড়িয়ে ধরতে পারে। এতে দেখা দিতে পারে বিপত্তি। ডুবন্ত কাউকে সাহায্য করতে গেলে সাবধান থাকতে হবে। তাই ডুবন্ত ব্যক্তিকে পেছন থেকে হাতসহ জড়িয়ে ধরে পানি থেকে তুলতে পারেন। এ ছাড়া লাইফ লাইন থাকলে তা ছুঁড়েও ডুবন্ত ব্যক্তিকে উদ্ধার করতে পারেন। লাইফ লাইন না থাকলে যেসব বস্তু পানিতে ভাসে এবং ডুবে যাওয়া ব্যক্তির ওজন বহন করতে পারে এমন বস্তু পানিতে ছুঁড়ে মারতে হবে। উদ্ধারকারী সাঁতার না জানলে ডুবন্ত ব্যক্তিকে ঠেলে ঠেলে তীরের দিকে নিয়ে যেতে হবে।"
          "\n২. পানি থেকে তোলার পর উপুড় করে দেখতে হবে শ্বাস-প্রশ্বাস আছে কি না। নাম ধরে ডাক দিয়েও দেখা যেতে পারে তিনি সাড়া দেন কি না। যদি শ্বাস-প্রশ্বাস না থাকে বা শ্বাস নেওয়া কষ্টকর হয়, তাহলে খেয়াল করতে হবে শ্বাসনালীর কোথাও কিছু আটকে আছে কি না। এ জন্য আঙুল দিয়ে মুখের মধ্যে কাদা-মাটি থাকলে তা বের করে দিতে হবে। তার পরও শ্বাস না নিলে মাথা টানটান করে ধরে মুখ হা করাতে হবে। এবার উদ্ধারকারী ব্যক্তিকে বুক ভরে শ্বাস নিতে হবে এবং ডুবন্ত ব্যক্তির মুখের সঙ্গে এমনভাবে মুখ লাগাতে হবে যেন কোনো ফাঁকা না থাকে। শিশু বা কম বয়সী হলে নাক-মুখ একসঙ্গে মুখের মধ্যে পুড়তে হবে আর বয়স্ক ব্যক্তি হলে নাক হাত দিয়ে চেপে ধরে মুখে মুখ লাগাতে হবে। এ অবস্থায় উদ্ধারকারী জোরে শ্বাস নিয়ে ডুবন্ত ব্যক্তির মুখে মুখ লাগিয়ে শ্বাস দিতে হবে। দেখতে হবে যে, শ্বাস দেওয়ার ফলে ডুবন্ত ব্যক্তির পেট ফুলে যায় কি না। যদি পেট ফুলে যায়, তাহলে বোঝা যাবে যে কৃত্রিম উপায়ে এভাবে শ্বাস দেওয়া ঠিকমতই হচ্ছে। ডুবন্ত ব্যক্তি নিজে থেকে শ্বাস না নেওয়া পর্যন্ত এভাবে চালাতে হবে।"
          "\n৩. কৃত্রিমভাবে শ্বাস প্রদানের পাশাপাশি হাত ধরে কিংবা গলার উঁচু অংশ তথা অ্যাডামস অ্যাপেলের একপাশে হাত দিয়ে দেখতে হবে যে, নাড়ির স্পন্দন আছে কি না। যদি না থাকে তাহলে বুকে চাপ দিতে হবে। বুকের বামপাশে হাত রেখে জোরে জোরে চাপ দিতে হবে, যেন বুক বেশ খানিকটা ধেবে যায়। যদি ডুবন্ত ব্যক্তি এক থেকে দুই বছরের শিশু হয়, তাহলে শিশুর বুক দুই হাত দিয়ে ধরে বুড়ো আঙুল দিয়ে চাপ দিতে হবে। এভাবে প্রতি ত্রিশবার চাপ দেওয়ার পর পূর্বের মতো দুইবার করে শ্বাস দিতে হবে। নাড়ির গতি স্বাভাবিক না হওয়া পর্যন্ত এরকম চক্রাকারে চালাতে হবে। এছাড়া প্রাথমিক চিকিৎসা চলার পাশাপাশি দ্রুত হাসপাতালে নেওয়ার ব্যবস্থাও করতে হবে।\n৪. শিশু পানিতে থাকার কারণে শরীরের তাপমাত্রা কমে যায়। তাই শরীর গরম রাখার জন্য কাপড়-চোপড় দিয়ে ভালো করে ঢেকে রাখা উচিত। রোগীর অবস্থা ভালো থাকলে এবং শ্বাস-প্রশ্বাস নিলে ও হৃৎস্পন্দন চালু থাকলে তাকে কুসুম গরম দুধ, চা ইত্যাদি খেতে দেওয়া যেতে পারে।"),
  PrimaryTreatmentData(
      title: "শ্বাসনালী আটকানো",
      define:
          "কারও গলায় খাবার আটকে গেলে তাঁর প্রাণ বাঁচাতে পারেন আপনিও,\nরেস্তঁরায় খাবার খেতে খেতে জমিয়ে গল্প করছিলেন বন্ধুরা। আচমকা এক জনের মুখ লাল হয়ে দম আটকে এল। কিছু বুঝে ওঠার আগেই সব শেষ।\nশিশুস্বাস্থ্য বিশেষজ্ঞ সৌমিত্র দত্ত জানালেন, গলায় খাবার আটকে যাওয়ার সমস্যা সবচেয়ে বেশি দেখা যায় শিশু আর বয়স্ক মানুষের মধ্যে।\nতাড়াহুড়ো করে খাবার খেতে গিয়ে বা খাওয়ার সময় কথা বলতে গিয়ে দম আটকে খুব কষ্টকর পরিস্থিতির শিকার হতে হয়। খাবার শুকনো ও শক্ত হলে গলায় আটকে যাবার আশঙ্কা বাড়ে। তাই ছোট ছোট গ্রাসে খাবার খান ও খাওয়ার সময় কথা কম বলুন।",
      treatpt:
          "\n\nতবে এই সমস্যা মোকাবিলার উপায় আছে।\nশ্বাসনালীর মুখে খাবার আটকে গেলে অক্সিজেন চলাচল খুব কমে যায়। এমনকি পুরোপুরি বন্ধও হয়ে যেতে পারে। এ ক্ষেত্রে দ্রুত আটকে যাওয়া খাবারের টুকরো বের না করে দিলে রোগীকে বাঁচানো যায় না। কোনও মানুষকে এই পরিস্থিতিতে পড়তে দেখলে হেইমলিচ ম্যানেউভারের সাহায্যে তাঁকে সুস্থ করে তোলা যায়\nঅসুস্থ মানুষটিকে পিছন থেকে জড়িয়ে দুই হাত দিয়ে পেটের ওপরের দিকে জোরে জোরে চাপ দিলে শ্বাসনালীতে আটকে থাকা খাবার বেরিয়ে আসে। "
          "\nইউটিউব বা ইন্টারনেটে খুঁজলে এমন ভিডিয়ো পাওয়া যায়। যা থেকে সহজেই শিখে নেওয়া যেতে পারে এই পদ্ধতি।\nশ্বাসনালীতে কিছু আটকে গেলে ফুসফুসে অক্সিজেন সরবরাহ বন্ধ হয়ে যায়। চিকিৎসা পরিভাষায় একে বলে অ্যাস্পিক্সিয়া। শ্বাসনালী একেবারে বন্ধ হয়ে গেলে, হৃদযন্ত্র ও মস্তিষ্ক অক্সিজেনের অভাবে কাজ করতে পারে না। অবস্থা সংকটজনক হয়ে ওঠে।\nখাবার আটকে গেলে পিঠে চাপড় মেরে খুব ভাল কাজ হয় না। বরং হেইমলিচ ম্যানিউভার পদ্ধতিতে রোগীকে পিছন থেকে জড়িয়ে ধরে ডায়াফ্রাম অর্থাৎ পেট ও পাঁজরের সংযোগস্থলে দু’হাতে জোরে ধাক্কা দিলে আটকানো খাবারের টুকরো বেড়িয়ে আসে। এ ভাবে অনেকের প্রাণ বাঁচানো যায়।"),
  PrimaryTreatmentData(
      title: "হার্ট এট্রাক",
      define:
          "হার্ট অ্যাটাকের লক্ষণ-\n১। বুকে ক্রমাগত ব্যথা, ছড়িয়ে পড়তে পারে চোয়াল, কাঁধ, দাঁত, গলা, হাতে।\n২। হঠাত্ পালস রেট খুব বেড়ে যাওয়া বা একেবারে কমে যাওয়া।\n৩। অতিরিক্ত ঘাম\n৪। বুকে মাঝখানে অস্বস্তিকর চাপ অনুভব করা, ভারী ভারী ভাব।\n৫। শ্বাস ছোট হয়ে আসা।\n৬। মাথা ঘোরা, জ্ঞান হারানো।\n৭। বমি বমি ভাব।\nএই অবস্থায় যত তাড়াতাড়ি সম্ভব হাসপাতালে নিয়ে যাওয়া জরুরি। অবিলম্বে অ্যাম্বুলেন্স কল করুন বা ডাক্তারকে ফোন করুন। বাড়িতে নিজের গাড়ি থাকলে নিজেরাও নিয়ে যেতে পারেন। তবে খেয়াল রাখবেন যেন রোগীর সঙ্গে সারাক্ষণ কেউ থাকে।",
      treatpt:
          "\n\nঅ্যাম্বুলেন্স বা ডাক্তার আসার আগে কী ভাবে ফার্স্ট এড দেবেন-\n১। প্রথমেই রোগীকে রিল্যাক্সড অবস্থায় নিয়ে আসুন। দেওয়ালে হেলান দিয়ে মাটিতে বসান। ঘাড়, মাথা কাঁধ হেলান দিয়ে হাঁটু মুড়ে রোগীকে বসালে রক্তচাপ কমবে।\n২। রোগীর যদি অ্যাসপিরিনে অ্যালার্জি না থাকে তবে অ্যাসপিরিন দিন। এই সময় ৩০০ গ্রাম অ্যাসপিরিন চিবিয়ে খেতে পারলে ধাক্কা অনেকটাই সামলানো যাবে।\n৩। এই সময় রোগী শক পেতে পারেন। জীবনের ঝুঁকি রয়েছে বুঝতে পারলে শক পাওয়া খুব স্বাভাবিক।\n৪। ক্রমাগত শ্বাস, পালস রেট ও রোগী কেমন সাড়া দিচ্ছেন তা চেক করতে থাকুন।\n৫। রোগী যদি অজ্ঞান হয়ে যায় তবে সিপিআর-এর সাহায্য নিন।"),
  PrimaryTreatmentData(
      title: "মাথা ঘুরে পরা",
      define: "",
      treatpt:
          "\n\nআপনার হঠাৎ মাথা ঘুরতে থাকলে আপনি যে কাজটা করছিলেন সেই কাজ করা থেকে নিজেকে বিরত রাখুন। চিৎ হয়ে শুয়ে পড়ুন। বন্ধ করুন চোখ দুটো। সহজভাবে শ্বাস নিন এবং সাহায্যের জন্য কাউকে ডাকুন।\nআপনি গাড়ি চালাতে থাকলে পা ব্রেকের ওপর রাখুন এবং থেমে পড়ুন। শুয়ে পড়ুন পাশের আসনে। ভাগ্যের ওপর নিজেকে ছাড়বেন না কখনোই। আপনি দুর্ঘটনা ঘটিয়ে ফেলতে পারেন, যদি আপনার মাথা ঘোরা মারাত্মক হয়।\nআপনি অতিরিক্ত পরিশ্রম করলে মাথা ঘোরার অভিজ্ঞতা হতে পারে। কাজ করবেন পরিকল্পনা মাফিক এবং আপনার সাধ্যের মধ্যে। কখনো সীমা অতিক্রম করবেন না। আগে চিন্তা করে তারপর কাজে হাত দেবেন।\nদীর্ঘক্ষণ না খেয়ে থাকবেন না। কাজের চাপে এক বেলার খাবার না খেলে এবং রক্তে চিনির মাত্রা কমে গেলে দ্রুত খেয়ে নিন। রক্তে চিনির মাত্রা কমে গেলে আপনার মাথা ব্যথা ও মাথা ঘোরার সমস্যা দেখা দিতে পারে। "
          "\nপর্যাপ্ত পানি পান করতে হবে\nবিশেষ করে গরমের সময় প্রচুর তরল পান করবেন। শরীরে পানির পরিমাণ কমে গেলে মাথা ঘোরার সমস্যা দেখা দেয়। তাই শরীর যাতে পানিশূন্য হয়ে না পড়ে সে ব্যাপারে সতর্ক থাকতে হবে। শরীরকে সব সময় পানিপূর্ণ রাখবেন।\nসতর্কতার সঙ্গে ওষুধ সেবন\nঅনেক ওষুধ আমরা সচরাচর গ্রহণ করে থাকি। যেমন-অ্যান্টিহিস্টামিন, অ্যান্টিবায়োটিক, উচ্চরক্তচাপের ওষুধ ও আলসারের ওষুধ। এসব ওষুধ মাথা ঘোরার উপসর্গ সৃষ্টি করতে পারে। কোনো ওষুধ গ্রহণে এমন সমস্যা হলে ডাক্তারকে জানান। তিনি ওষুধ পরিবর্তন করে দেবেন।\nমাথা ঘোরা অবহেলা করবেন না\nআপনার মাঝেমধ্যেই মাথা ঝিমঝিম করলে কিংবা মাথা ঘুরলে তা অবহেলা করবেন না। ওপরের পরামর্শ মেনে চলুন এবং চিকিৎসকের সঙ্গে কথা বলুন। "),
  PrimaryTreatmentData(
      title: "অজ্ঞান হলে",
      define:
          "চলতি পথে, কর্মস্থলে, বাড়িতে হঠাৎ কাউকে অচেতন হয়ে পড়তে দেখলে আপনি কী করবেন? অসুস্থ ব্যক্তির সহায়তায় এগিয়ে যাওয়া কর্তব্য। এমন পরিস্থিতিতে ঘাবড়ে না গিয়ে প্রাথমিক কিছু পদক্ষেপ নিলে হয়তো মানুষটার জীবন রক্ষা পাবে। নানা কারণে মানুষ সংজ্ঞা হারিয়ে ফেলতে পারে। বুকে বা মাথায় আঘাত, অতিরিক্ত রক্তক্ষরণ, শরীরে পানিশূন্যতা বা লবণশূন্যতা, রক্তে শর্করার মাত্রা হ্রাস, হঠাৎ রক্তচাপ হ্রাস, হৃদ্‌রোগ, মস্তিষ্কে রক্ত সরবরাহে ঘাটতি, মস্তিষ্কে রক্তক্ষরণ বা স্ট্রোক, মৃগীরোগ ইত্যাদি কারণে মানুষ আচমকা জ্ঞান হারায়। ওষুধ বা অ্যালকোহলের প্রতিক্রিয়া, এমনকি বিষক্রিয়াও হতে পারে কারণ। এমন অবস্থায়, কারণ যখন জানা নেই বা বুঝতে পারছেন না কিছু, তখন সেই মুহূর্তে আপনি কী করতে পারেন?",
      treatpt:
          "\n\nকয়েকটি পরামর্শ:\n*  প্রথমে রোগীর চেতনা কতটুকু আছে বোঝার চেষ্টা করুন। জোরে ডাক দিয়ে দেখুন সাড়া দেয় কি না।\n* নাকের কাছে হাত দিয়ে বা বুকের ওঠানামা দেখে বোঝার চেষ্টা করুন শ্বাস-প্রশ্বাস চলছে কি না।\n* আক্রান্ত ব্যক্তিকে সোজা করে শুইয়ে দিন। বেশি নাড়াচড়া করবেন না বা স্থান বদলাবেন না। কারণ, আপনি জানেন না পড়ে গিয়ে কোনো হাড় ভেঙেছে কি না। সংজ্ঞা না থাকলে একটু একদিকে কাত করে রাখুন, চিত বা উপুড় না করে। এতে মুখে জমা লালা গলায় আটকে যাবে না।\n* ব্যবহৃত পোশাক ঢিলা করে দিন। পা দুটো একটু উঁচু করে দিন। মাথা পেছন দিকে কাত করে থুতনি উঁচু করে ধরুন।"
          "\n* নিশ্বাস বন্ধ থাকলে মুখে মুখ লাগিয়ে জোরে বাতাস দিতে পারেন। দেখবেন বুক ওঠানামা করছে কি না। খিঁচুনি হতে থাকলে চেপে না ধরে খোলামেলা জায়গায় কাত করে শুইয়ে রাখুন।\n* অচেতন ব্যক্তিকে কিছু খাওয়ানোর চেষ্টা করবেন না, মুখে পানি দেবেন না, স্যান্ডেল জুতা মুখের কাছে ধরার দরকার নেই।\n* ডায়াবেটিস আছে জানা থাকলে সম্ভব হলে রক্তে শর্করার মাত্রা মাপুন। অথবা না মেপেই মুখের ভেতর খানিকটা চিনি দিতে পারেন।\n* শরীর থেকে ইঁদুর মারার বিষ বা কীটনাশকের গন্ধ পেলে দ্রুত সব জামাকাপড় খুলে দিন ও পারলে ত্বক পানি দিয়ে ধুয়ে দিন।\n* রক্তচাপ কম থাকলে পায়ের দিক উঁচু করে দিন।\nপ্রাথমিক চিকিৎসা দেওয়ার পর আক্রান্ত ব্যক্তিকে দ্রুত নিকটস্থ হাসপাতালে স্থানান্তর করুন। "),
  PrimaryTreatmentData(
      title: "মৃগী রোগ",
      define: "",
      treatpt:
          "\n\nকয়েকটি পরামর্শ:\nরোগী দাঁড়ানো বা চেয়ারে বসা অবস্থায় খিঁচুনীতে আক্রান্ত হলে তাকে আলতো করে ধরে মেঝেতে শুইয়ে দিন অথবা এমন ব্যবস্থা রাখুন, যাতে রোগী পড়ে গিয়ে মাথায় আঘাত না পায়।\nরোগীর মাথার নিচে বালিশ বা নরম কোনও কাপড়  জাতীয় কিছু দিন। খিঁচুনী স্বাভাবিকভাবে শেষ হতে দিন। এই সময় আক্রান্তকে চেপে ধরবেন না।\nরোগীর মুখে জোর করে আঙুল বা অন্য কিছু ঢোকানোর চেষ্টা করবেন না। রোগীর জিহ্বায় দাঁত দিয়ে কামড় লাগলেও খিঁচুনী থাকা অবস্থায় তা ছাড়ানোর জন্য জোর করা উচিত নয়।\nখিঁচুনী শেষ হলে রোগীকে এক পাশে কাত করে শুইয়ে দিন। আক্রান্তের পোশাক হালকা করে দিন। রোগীর আশেপাশে বেশি ভীড় জমতে দেবেন না।"
          "\nখিঁচুনী যদি ৫ মিনিটের বেশি স্থায়ী হয়, কিংবা রোগীর একবার খিঁচুনীর পর জ্ঞান ফেরার আগেই দ্বিতীয় খিঁচুনী চলে আসে তা হলে রোগীকে দ্রুত হাসপাতাল অথবা চিকিৎসকের কাছে নিয়ে যান।\nখিঁচুনী শেষ হলে রোগীর নাড়ীর স্পন্দন অনুভব করুন, রোগী ঠিক মতো শ্বাস নিতে পারছে কি না সেদিকে নজর রাখুন। শ্বাস-প্রশ্বাস ব্যাহত হয় এমন কোনও কিছু মুখে বা নাকে থাকলে তা সরিয়ে দিন।\nএই কয়েকটি বিষয় মাথায় রাখলেই আপনার সাহায্যে একটি প্রাণ বাঁচতে পারবে। "),
  //PrimaryTreatmentData(title: "", define: "", treatpt: ""),
];

